import React, { useState, useEffect } from "react";

function App() {
  const [pubs, setPubs] = useState([]);

  useEffect(() => {
    // Fetch pubs from our backend endpoint
    fetch("http://localhost:3001/api/pubs")
      .then((response) => response.json())
      .then((data) => {
        setPubs(data);
      })
      .catch((error) => {
        console.error("Error fetching pubs:", error);
      });
  }, []);

  return (
    <div>
      <h1>List of Pubs</h1>
      <ul>
        {pubs.map((pub) => (
          <li key={pub.id}>
            <h2>{pub.name}</h2>
            <p>Location: {pub.location}</p>
            <p>{pub.description}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
